module SimpleAdder(
    input [3:0] a,
    input [3:0] b,
    output reg [3:0] sum
);

    // Implement a simple adder using full adders
    wire carry;
    FullAdder FA1(a[0], b[0], carry, sum[0]);
    FullAdder FA2(a[1], b[1], carry, sum[1]);
    FullAdder FA3(a[2], b[2], carry, sum[2]);
    FullAdder FA4(a[3], b[3], carry, sum[3]);

endmodule

module FullAdder(
    input a,
    input b,
    input c_in,
    output reg c_out,
    output reg sum
);

    // Implement a full adder
    assign sum = (a ^ b) ^ c_in;
    assign c_out = (a & b) | (b & c_in) | (a & c_in);

endmodule

