with_steering_implementation:
module TopModule (
    input [31:0] in,
    output [31:0] out,
    output error
);

    reg [31:0] reversed;
    reg error;

    always @(*) begin
        error = 1'b0; // Initialize error flag as false
        reversed = {in[7:0], in[15:8], in[23:16], in[31:24]};

        // Check if there is more than one '1' in the input vector
        for (int i = 0; i < 32; i++) begin
            if (in[i] == 1'b1) begin
                error = 1'b1; // Set error flag to true if more than one '1' is found
                break; // Exit the loop once an error is detected
            end
        end
    end

    assign out = reversed;

endmodule

