module TopModule (
    input [2:0] in,
    output [1:0] out
);

    // Initialize the counter to zero
    reg [2:0] count = 3'b0;

    // Iterate over each bit of the input
    always @(*) begin
        // Increment the counter if the current bit is '1'
        if (in[2]) count = count + 1;
        if (in[1]) count = count + 1;
        if (in[0]) count = count + 1;
    end

    // Assign the counter value to the output
    assign out = count[2:1];

endmodule

