module TopModule (
    input x,
    input y,
    input w,
    output z,
    output v
);

    // Compute the XOR of x and y
    wire xor_result1 = x ^ y;

    // Compute the XOR of the first XOR result and w
    wire xor_result2 = xor_result1 ^ w;

    // Compute the AND of the second XOR result and x
    wire and_result = xor_result2 & x;

    // Assign the result to the output z
    assign z = and_result;

    // Assign the result to the output v
    assign v = and_result;

endmodule

