module Adder(
    input [7:0] a, 
    input [7:0] b, 
    output reg [7:0] sum
);

    // Define the carry signals
    reg [7:0] c1, c2;

    // Full adder for each bit position
    always @(*) begin
        sum[0] = a[0] ^ b[0] ^ c1;
        c2 = (a[0] & b[0]) | (a[0] & c1) | (b[0] & c1);
    end

    // Propagate the carry signal
    always @(*) begin
        c1 = c2;
    end

endmodule

