module TopModule (
    input  [n-1:0] in,
    output [n-1:0] out
);

    // Define a temporary register to hold the reversed bits
    reg [n-1:0] reversed_bits;

    // Reverse the byte order of the input
    assign reversed_bits = {
        in[(n-1):(n-4)],
        in[(n-5):(n-8)],
        in[(n-9):(n-12)],
        in[(n-13):(n-16)],
        in[(n-17):(n-20)],
        in[(n-21):(n-24)],
        in[(n-25):(n-28)],
        in[(n-29):(n-32)]
    };

    // Assign the reversed bits to the output
    assign out = reversed_bits;

endmodule

