module TopModule (
    input  in  [31:0], // 32-bit input vector
    output out [31:0]  // 32-bit output vector
);

// Define a temporary register to hold the reversed bytes
reg [31:0] temp;

// Reverse the byte order by shifting and concatenating
always @(*) begin
    // Shift the first byte to the last position
    temp[31:24] = in[7:0];

    // Shift the second byte to the second last position
    temp[23:16] = in[15:8];

    // Shift the third byte to the third last position
    temp[15:8] = in[23:16];

    // Shift the fourth byte to the first position
    temp[7:0] = in[31:24];

    // Assign the reversed bytes to the output
    out = temp;
end

endmodule

