module TopModule (
    input wire x,
    input wire y,
    output wire z
);

    // Calculate the XOR of x and y
    wire xor_result = x ^ y;

    // Calculate the AND of xor_result and x
    assign z = xor_result & x;

    // Check if both inputs are equal
    wire are_equal = x == y;

    // Output the result of the check
    assign z = z | are_equal;

endmodule

