    input  in,
    output out
);

    reg [3:0] delay_counter;
    reg [3:0] max_delay;

    initial begin
        max_delay = 15; // Set the maximum delay in clock cycles
        delay_counter = 0;
    end

    always @(posedge clk) begin
        if (delay_counter < max_delay) begin
            delay_counter <= delay_counter + 1;
            out <= in;
        end else begin
            out <= in;
            delay_counter <= 0;
        end
    end

endmodule

