module TopModule (
    input wire clk,
    input wire rst_n,
    output reg zero
);

reg [29:0] counter; // 30-bit counter for 50MHz clock
reg [29:0] div_counter; // 30-bit counter for 1MHz clock

always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        counter <= 0;
        div_counter <= 0;
        zero <= 1'b0;
    end else begin
        if (counter == 49999999) begin // 50MHz clock period
            counter <= 0;
            div_counter <= div_counter + 1; // Increment 1MHz counter
            if (div_counter == 99999999) begin // 1MHz clock period
                div_counter <= 0;
                zero <= ~zero; // Toggle zero every 1MHz
            end
        end else begin
            counter <= counter + 1;
        end
    end
end

endmodule

