module TopModule (
    input [31:0] in,
    output [31:0] out
);

    reg [31:0] reversed;
    reg [31:0] half_in;
    reg [31:0] half_out;

    // Check if the input is a palindrome
    always @* begin
        half_in = in[15:0];
        half_out = reversed[15:0];

        if (half_in == half_out) begin
            // If the first half matches the second half, it's a palindrome
            out = reversed;
        end else begin
            // If the first half does not match the second half, set the output to 0
            out = 32'b0;
        end
    end

    // Reverse the byte order of the input
    always @* begin
        reversed = {
            in[7:0],
            in[15:8],
            in[23:16],
            in[31:24]
        };
    end

endmodule

