module TopModule (
    input  in [n-1:0], // n-bit input vector
    output out [n/2-1:0] // n/2-bit output representing population count
);

    // Internal signals
    wire [n-1:0] count;

    // Population count logic
    always @(*) begin
        count = 0;
        for (int i = 0; i < n; i++) begin
            if (in[i]) begin
                count = count + 1;
            end
        end
    end

    // Assign the population count to the output
    assign out = count[n/2-1:n/2-1];

endmodule

