module TopModule_TestBench;

reg [31:0] in;
wire [31:0] out;
TopModule top_module (.in(in), .out(out));

initial begin
    // Test case 1: Normal byte order
    in = 32'h12345678;
    #10; // Wait for 10 cycles
    $display("Input: %h, Output: %h", in, out);
    assert (out == 32'h87654321);

    // Test case 2: Byte order reversed
    in = 32'h9abcdef0;
    #10; // Wait for 10 cycles
    $display("Input: %h, Output: %h", in, out);
    assert (out == 32'h0facedb9);

    // Test case 3: All zeros
    in = 32'h00000000;
    #10; // Wait for 10 cycles
    $display("Input: %h, Output: %h", in, out);
    assert (out == 32'h00000000);

    // Test case 4: All ones
    in = 32'hffffffff;
    #10; // Wait for 10 cycles
    $display("Input: %h, Output: %h", in, out);
    assert (out == 32'hffffffff);

    $finish;
end

endmodule

