    TopModule top_module (
        .in(1'b0),  // Input signal
        .out(out)   // Output signal
    );

    // Define a variable to hold the output value
    reg out;

    // Create a task to monitor the output signal
    task monitor_output;
        $display("Time: %t, In: %b, Out: %b", $time, top_module.in, out);
    endtask

    // Create a clock signal
    reg clk;
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 5ns period
    end

    // Create a reset signal
    reg rst;
    initial begin
        rst = 1;  // Active high reset
        #10 rst = 0;  // Deassert reset after 10ns
    end

    // Create a main process to drive the simulation
    initial begin
        // Drive the input signal
        top_module.in = 1'b0;

        // Monitor the output signal
        forever begin
            #10 monitor_output();
        end
    end

endmodule

