module Delay (
    input  clk,
    input  rst,
    input  [7:0] data_in,
    output [7:0] data_out
);

    reg [7:0] internal_data;
    reg [4:0] counter;

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            internal_data <= 8'b0;
            counter <= 5'b0;
        end else begin
            if (counter == 5'b0) begin
                internal_data <= data_in;
            end
            counter <= counter + 1'b1;
        end
    end

    assign data_out = internal_data;

endmodule

