module TopModule (
    input  [31:0] in,
    output [31:0] out
);

    // Initialize the output as all zeros
    assign out = 32'h00000000;

    // Check if the input is a valid 32-bit vector
    logic [31:0] valid_in;
    always @(*) begin
        valid_in = (in == 32'h00000000);
    end

    // Reverse the byte order only if the input is valid
    genvar i;
    generate
        if (valid_in) begin
            for (i = 0; i < 4; i++) begin
                // Extract a byte from the input
                assign out[8*i +:8] = in[31 - 8*i +:8];
            end
        end else begin
            // If the input is not valid, set the output to all ones
            assign out = 32'hFFFFFFFF;
        end
    endgenerate

endmodule

