module TopModule (
    input [2:0] in,
    output [1:0] out
);

    reg [2:0] count;

    always @(in) begin
        if (in == 3'b111) begin
            count = 3'b111;
        end else if (in == 3'b110) begin
            count = 3'b110;
        end else if (in == 3'b101) begin
            count = 3'b101;
        end else if (in == 3'b100) begin
            count = 3'b100;
        end else if (in == 3'b011) begin
            count = 3'b011;
        end else if (in == 3'b010) begin
            count = 3'b010;
        end else if (in == 3'b001) begin
            count = 3'b001;
        end else if (in == 3'b000) begin
            count = 3'b000;
        end
    end

    assign out = count[2:1];

endmodule

