    input wire clk,
    input wire rst,
    input wire en,
    input wire [3:0] led_data,
    output reg [3:0] display
);

    reg [3:0] count;

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            count <= 0;
        end else if (en) begin
            count <= count + 1;
        end
    end

    always @(*) begin
        if (count == 15) begin
            display <= 4'b0000;
        end else begin
            display <= count;
        end
    end

    assign led_data = display;

endmodule

