module TopModule (
    input [N-1:0] in, // N is the number of input bits
    output [M-1:0] out // M is the number of output bits (typically floor(log2(N)))
);

    // Define internal signals
    wire [N-1:0] temp;
    wire [M-1:0] count;

    // Count the number of '1's in the input vector
    assign temp = in;
    assign count = (temp & 1) + (temp >> 1 & 1) + (temp >> 2 & 1) + ... + (temp >> (N-1) & 1);

    // Output the population count
    assign out = count;

endmodule

