module TopModule (
    input  clk,
    input  reset,
    input  in,
    output out
);

// Internal register to hold the current state of the NOT gate
reg [0:0] current_out;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        // Reset the output to 1 (assuming a high impedance state)
        current_out <= 1'b1;
    end else begin
        // Update the output based on the input
        current_out <= ~in;
    end
end

// Assign the current output to the output port
assign out = current_out;

endmodule

