module TopModule (
    input  in [n-1:0], // n-bit input
    output out [m-1:0], // m-bit output
    input reset // Reset signal
);

    // Internal signals
    reg [m-1:0] count;

    // Population count logic
    always @(posedge reset or posedge clk) begin
        if (reset) begin
            count = 0;
        end else begin
            count = 0;
            for (int i = 0; i < n; i++) begin
                if (in[i]) begin
                    count++;
                end
            end
        end
    end

    // Assign output
    assign out = count;

endmodule

