module TopModule(
    input [2:0] in,
    output reg [1:0] out
);

    // Initialize a counter to zero
    reg [1:0] count = 2'b0;

    // Iterate over each bit in the input vector
    always @(*) begin
        // Increment the counter for each '1' found in the input vector
        if (in[2]) count = count + 2'b1;
        if (in[1]) count = count + 2'b1;
        if (in[0]) count = count + 2'b1;
    end

    // Assign the counter value to the output
    assign out = count;

endmodule

