module TopModule (
    input  [2:0] in,
    output [1:0] out
);

    reg [2:0] count;

    // Population count logic using a counter
    always @(*) begin
        count = 0;
        for (int i = 0; i < 3; i++) begin
            if (in[i]) begin
                count = count + 1'b1;
            end
        end
    end

    // Map the count to the output
    assign out = count[1:0];

endmodule

