module SimpleAdder(
    input [3:0] A4,
    input [3:0] B4,
    output [3:0] Sum4,
    output Overflow,
    output Underflow
);

    // Calculate the sum of A4 and B4
    assign Sum4 = A4 + B4;

    // Determine overflow
    assign Overflow = (A4[3] & ~B4[3] & ~Sum4[3]) | (~A4[3] & B4[3] & ~Sum4[3]);

    // Determine underflow
    assign Underflow = (~A4[3] & ~B4[3] & Sum4[3]) | (A4[3] & B4[3] & Sum4[3]);

endmodule

