module TopModule (
    input  in  [31:0],
    output out [31:0]
);

    // Create a temporary variable to store the reversed bytes
    reg [31:0] temp;

    // Use a loop to reverse each byte
    for (int i = 0; i < 4; i++) begin
        // Extract the byte from the input
        reg [7:0] byte = in[7 + 8*i : 8*i];

        // Shift the byte into the correct position in the temporary variable
        temp[7 + 8*i : 8*i] = byte;
    end

    // Assign the reversed bytes to the output
    assign out = temp;

endmodule

