module MaxOfThree (
    input wire a,
    input wire b,
    input wire c,
    output reg max
);

    // Helper function to find the maximum of two numbers
    function [1:0] max2 (input [1:0] x, input [1:0] y);
        if (x > y) begin
            max2 = 2'b10;
        end else begin
            max2 = 2'b01;
        end
    endfunction

    // Find the maximum of the first two numbers
    reg [1:0] temp_max = max2(a, b);

    // Find the maximum of the result and the third number
    max = max2(temp_max, c);

endmodule

