    reg [31:0] temp;

    // Loop through each byte of the input
    for (genvar i = 0; i < 4; i++) begin
        // Extract the i-th byte from the input
        reg [7:0] byte = in[(i+1)*8-1 : i*8];

        // Shift the byte to the correct position in the temporary register
        temp[(i*8) +: 8] = byte;
    end

    // Assign the reversed bytes to the output
    assign out = temp;

endmodule

