module TopModule (
    input [N-1:0] in,
    output reg [N-1:0] out
);

parameter N = 32;

always @(*) begin
    // Initialize the output to all zeros
    out = N'h0;

    // Iterate over each byte of the input
    for (int i = 0; i < (N/8); i++) begin
        // Shift the input by 8 bits to get the current byte
        byte = in[7 + 8*i : 8*i];

        // Shift the byte to the correct position in the output
        out[7 + 8*i : 8*i] = byte;
    end
end

endmodule

