module TopModule (
    input [31:0] in,
    output [31:0] out
);

    // Declare a temporary register to hold the reversed bytes
    reg [31:0] temp;

    // Loop through each byte of the input vector
    for (genvar i = 0; i < 4; i++) begin
        // Extract the current byte from the input vector
        reg [7:0] byte = in[(i+1)*8-1 : i*8];

        // Shift the byte left by 24 bits to position it at the correct place in the output vector
        byte = {byte[7:0], byte[7:0], byte[7:0], byte[7:0]};

        // Assign the byte to the corresponding position in the output vector
        temp[(i+1)*8-1 : i*8] = byte;
    end

    // Assign the reversed bytes to the output vector
    assign out = temp;

endmodule

