// Inputs:
//   a: First input bit
//   b: Second input bit
// Outputs:
//   out: Output of the XNOR gate

module TopModule(
    input  a,  // First input bit
    input  b,  // Second input bit
    output out  // Output of the XNOR gate
);

// Always block to compute the output of the XNOR gate
always @(*) begin
    // The XNOR gate can be implemented using the ~(a ^ b) expression
    // ~(a ^ b) evaluates to 1 if a and b are different, and 0 if they are the same
    out = ~(a ^ b);
end

endmodule

