// Testbench for TopModule module
module TopModule_tb;
    // Declare signals
    reg in1, in2;
    wire out;

    // Instantiate TopModule module
    TopModule top_module (
        .in1(in1),
        .in2(in2),
        .out(out)
    );

    // Test cases
    initial begin
        // Test case 1: in1 = 0, in2 = 0
        in1 = 0;
        in2 = 0;
        #10; // Wait for 10ns
        $display("Test case 1: in1 = %b, in2 = %b, out = %b", in1, in2, out);
        assert (out == 1'b1) else $error("Test case 1 failed");

        // Test case 2: in1 = 0, in2 = 1
        in1 = 0;
        in2 = 1;
        #10; // Wait for 10ns
        $display("Test case 2: in1 = %b, in2 = %b, out = %b", in1, in2, out);
        assert (out == 1'b0) else $error("Test case 2 failed");

        // Test case 3: in1 = 1, in2 = 0
        in1 = 1;
        in2 = 0;
        #10; // Wait for 10ns
        $display("Test case 3: in1 = %b, in2 = %b, out = %b", in1, in2, out);
        assert (out == 1'b0) else $error("Test case 3 failed");

        // Test case 4: in1 = 1, in2 = 1
        in1 = 1;
        in2 = 1;
        #10; // Wait for 10ns
        $display("Test case 4: in1 = %b, in2 = %b, out = %b", in1, in2, out);
        assert (out == 1'b0) else $error("Test case 4 failed");

        // Finish simulation
        $finish;
    end
endmodule

