module TopModule (
    input [31:0] in,
    input control,
    output [31:0] out
);

    reg [7:0] b0, b1, b2, b3;
    reg [31:0] result;

    always @(in or control) begin
        if (control) begin
            // Reverse byte order
            b0 = in[31:24];
            b1 = in[23:16];
            b2 = in[15:8];
            b3 = in[7:0];
            result = {b3, b2, b1, b0};
        end else begin
            // Normal byte order
            b0 = in[0:7];
            b1 = in[8:15];
            b2 = in[16:23];
            b3 = in[24:31];
            result = {b0, b1, b2, b3};
        end
    end

    assign out = result;

endmodule

