module TopModule (
    input x,
    input y,
    output z,
    output reg counter
);

    // Define intermediate variables
    wire xor_result;
    wire and_result;

    // Compute the XOR result
    xor_result = x ^ y;

    // Compute the AND result
    and_result = xor_result & x;

    // Assign the final result to z
    assign z = and_result;

    // Counter logic
    always @(posedge x or posedge y or posedge x and y) begin
        if ((x == 0 && y == 1) || (x == 1 && y == 0)) begin
            counter <= 0;
        end else if ((x == 0 && y == 0) || (x == 1 && y == 1)) begin
            counter <= counter + 1;
        end
    end

endmodule

