    TopModule top_module (
        .in1(1'b0),
        .in2(1'b0),
        .out(out)
    );

    // Create a wire to hold the expected output
    reg expected_out;

    // Create a wire to hold the actual output
    wire actual_out;

    // Assign the actual output to the wire
    assign actual_out = top_module.out;

    // Create a always block to simulate the input
    always #5 begin
        in1 = 1'b1;
        in2 = 1'b0;
        #5;
        in1 = 1'b0;
        in2 = 1'b1;
        #5;
        in1 = 1'b1;
        in2 = 1'b1;
        #5;
        in1 = 1'b0;
        in2 = 1'b0;
    end

    // Create a always block to check the output
    always @* begin
        expected_out = ~in1 & ~in2;
        $display("in1 = %b, in2 = %b, expected_out = %b, actual_out = %b", in1, in2, expected_out, actual_out);
        assert (actual_out == expected_out) else $error("Test failed!");
    end

endmodule

