module TopModule (
    input wire clk,
    input wire rst_n,
    output reg one
);

always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        one <= 0; // Reset condition: drive '0' on 'one'
    end else begin
        one <= 1; // Normal operation: drive '1' on 'one'
    end
end

endmodule

