module TopModule(
    input [2:0] in,
    output [1:0] out
);

    // Internal logic for population count
    reg [2:0] count = 0;

    // Increment count for each '1' in the input
    always @(in) begin
        if (in[2]) count = count + 1;
        if (in[1]) count = count + 1;
        if (in[0]) count = count + 1;
    end

    // Output the population count as two bits
    assign out = count[2:1];

endmodule

