        shift_amount[2] ? in[10:8] : in[10:9],
        shift_amount[1] ? in[7:5] : in[7:6],
        shift_amount[0] ? in[4:2] : in[4:3],
        shift_amount[4] ? in[1:0] : in[1:1]
    };

    // Reverse the byte order
    assign temp = {
        temp[7:0],
        temp[15:8],
        temp[23:16],
        temp[31:24]
    };

    // Assign the reversed value to the output
    assign out = temp;

