module TopModule (
    input [31:0] in,
    output [31:0] out
);

    // Split the input into four 8-bit registers
    reg [7:0] byte0, byte1, byte2, byte3;

    // Assign each byte from the input to its respective register
    assign byte0 = in[7:0];
    assign byte1 = in[15:8];
    assign byte2 = in[23:16];
    assign byte3 = in[31:24];

    // Combine the bytes back into a single 32-bit output
    assign out = {byte3, byte2, byte1, byte0};

endmodule

