        $display("Starting simulation");

        // Test case 1: Input = 0, Expected Output = 1
        in = 0;
        #10; // Wait for 10 cycles
        $display("Test Case 1: in = %b, out = %b", in, out);
        assert (out == 1) else $error("Test Case 1 Failed");

        // Test case 2: Input = 1, Expected Output = 0
        in = 1;
        #10; // Wait for 10 cycles
        $display("Test Case 2: in = %b, out = %b", in, out);
        assert (out == 0) else $error("Test Case 2 Failed");

        $finish();
    end

endmodule

