module TopModule (
    input  [31:0] in,
    output [31:0] out
);

    // Create a temporary register to hold the reversed bytes
    reg [31:0] temp;

    // Check if the input is valid
    always @(*) begin
        if (!in) begin
            $error("Input is null or undefined");
            temp = 32'b0; // Set default value to avoid errors
            out = 32'b0;
        end else begin
            // Loop through each byte of the input
            for (int i = 0; i < 4; i++) begin
                // Extract the current byte from the input
                reg [7:0] byte = in[7 + 8*i : 8*i];

                // Shift the byte left by 24 bits to position it correctly in the output
                byte = byte << 24;

                // Assign the shifted byte to the corresponding position in the temporary register
                temp[23 + 8*i : 16 + 8*i] = byte;
            end

            // Assign the temporary register to the output
            out = temp;
        end
    end

endmodule

