// Test cases for the TopModule
module test_TopModule;
    reg a, b;
    wire out;
    TopModule top_module (.a(a), .b(b), .out(out));

    initial begin
        $display("Testing TopModule...");

        // Test case 1: a = 0, b = 0, expected out = 1
        a = 0; b = 0;
        #10 $display("a = %b, b = %b, out = %b", a, b, out);
        assert (out == 1);

        // Test case 2: a = 0, b = 1, expected out = 0
        a = 0; b = 1;
        #10 $display("a = %b, b = %b, out = %b", a, b, out);
        assert (out == 0);

        // Test case 3: a = 1, b = 0, expected out = 0
        a = 1; b = 0;
        #10 $display("a = %b, b = %b, out = %b", a, b, out);
        assert (out == 0);

        // Test case 4: a = 1, b = 1, expected out = 1
        a = 1; b = 1;
        #10 $display("a = %b, b = %b, out = %b", a, b, out);
        assert (out == 1);

        $finish;
    end
endmodule

