module TopModule(
    input clk,
    input in,
    output out
);

    reg [1:0] state;

    always @(posedge clk) begin
        case (state)
            2'b00: state <= 2'b01;
            2'b01: state <= 2'b10;
            2'b10: state <= 2'b00;
            default: state <= 2'b00;
        endcase
    end

    always @(*) begin
        out = in & state[0];
    end

endmodule

