module TopModule_tb;

    reg [7:0] in;
    wire [7:0] out;

    TopModule dut (

        .in(in),

        .out(out)

    );

    initial begin

        // Test case 1: All zeros
        in = 8'b00000000;

        #10;

        assert(out == 8'b00000000) else $error("Test case 1 failed");

        // Test case 2: All ones
        in = 8'b11111111;

        #10;

        assert(out == 8'b11111111) else $error("Test case 2 failed");

        // Test case 3: Mixed values
        in = 8'b10101010;

        #10;

        assert(out == 8'b01010101) else $error("Test case 3 failed");

        // Test case 4: Single bit set
        in = 8'b00000001;

        #10;

        assert(out == 8'b10000000) else $error("Test case 4 failed");

        // Test case 5: Single bit clear
        in = 8'b11111110;

        #10;

        assert(out == 8'b00000001) else $error("Test case 5 failed");

        // Test case 6: Reverse of all zeros
        in = 8'b00000000;

        #10;

        assert(out == 8'b00000000) else $error("Test case 6 failed");

        // Test case 7: Reverse of all ones
        in = 8'b11111111;

        #10;

        assert(out == 8'b11111111) else $error("Test case 7 failed");

        // Test case 8: Reverse of mixed values
        in = 8'b10101010;

        #10;

        assert(out == 8'b01010101) else $error("Test case 8 failed");

        // Test case 9: Reverse of single bit set
        in = 8'b00000001;

        #10;

        assert(out == 8'b10000000) else $error("Test case 9 failed");

        // Test case 10: Reverse of single bit clear
        in = 8'b11111110;

        #10;

        assert(out == 8'b00000001) else $error("Test case 10 failed");

        $finish;

    end

endmodule

